import {
  Html5MediaElement
} from "./chunk.RVJZRTLD.js";

// src/providers/audio/AudioElement.ts
import { vdsEvent } from "@vidstack/foundation";
import { css } from "lit";
var AudioElement = class extends Html5MediaElement {
  static get styles() {
    return [
      css`
        :host {
          display: inline-block;
        }

        :host([hidden]) {
          display: none;
        }
      `
    ];
  }
  connectedCallback() {
    super.connectedCallback();
    this.dispatchEvent(
      vdsEvent("vds-view-type-change", {
        detail: "audio" /* Audio */
      })
    );
  }
};

export {
  AudioElement
};
