import {
  ToggleButtonElement
} from "./chunk.EZDBIN3S.js";
import {
  MediaRemoteControl
} from "./chunk.X5QPRJP2.js";
import {
  mediaStoreSubscription
} from "./chunk.O2H47C42.js";

// src/ui/play-button/PlayButtonElement.ts
import { setAttribute, setAttributeIfEmpty } from "@vidstack/foundation";
var PlayButtonElement = class extends ToggleButtonElement {
  constructor() {
    super();
    this._mediaRemote = new MediaRemoteControl(this);
    mediaStoreSubscription(this, "paused", ($paused) => {
      this.pressed = !$paused;
      setAttribute(this, "paused", $paused);
    });
  }
  connectedCallback() {
    super.connectedCallback();
    setAttributeIfEmpty(this, "aria-label", "Play");
  }
  _handleButtonClick(event) {
    if (this.disabled)
      return;
    if (this.pressed) {
      this._mediaRemote.pause(event);
    } else {
      this._mediaRemote.play(event);
    }
  }
};

export {
  PlayButtonElement
};
